import { useState } from "react";
import { useNavigate } from "react-router-dom";
import styles from "./Search.module.css";
import man from "../../assets/man3.jpg";

const Search = ({ profiles, subjects }) => {
  const navigate = useNavigate();

  // States to manage the search filters
  const [selectedSubject, setSelectedSubject] = useState("");
  const [name, setName] = useState("");
  const [surname, setSurname] = useState("");
  const [jobStatus, setJobStatus] = useState("");
  const [city, setCity] = useState("");

  // Function to handle filtering profiles based on the search criteria
  const handleSearch = () => {
    const filteredProfiles = profiles.filter((profile) => {
      const matchesSubject = selectedSubject
        ? profile.subject === selectedSubject
        : true;
      const matchesName = name
        ? profile.firstname.toLowerCase().includes(name.toLowerCase())
        : true;
      const matchesSurname = surname
        ? profile.lastname.toLowerCase().includes(surname.toLowerCase())
        : true;
      const matchesJobStatus = jobStatus
        ? profile.jobStatus.toLowerCase().includes(jobStatus.toLowerCase())
        : true;
      const matchesCity = city
        ? profile.city.toLowerCase().includes(city.toLowerCase())
        : true;

      return (
        matchesSubject &&
        matchesName &&
        matchesSurname &&
        matchesJobStatus &&
        matchesCity
      );
    });

    return filteredProfiles;
  };

  return (
    <div className={styles.search}>
      <div className={styles.critery}>
        <h5>Axtarış kriteriyalarını daxil edin</h5>
      </div>
      <div className={styles.critery_container}>
        <div className={styles.container_inputs}>
          {/* Subject filter */}
          <select
            className={styles.subject_width}
            value={selectedSubject}
            onChange={(e) => setSelectedSubject(e.target.value)}
          >
            <option value="">Bütün sahələr</option>
            {subjects.map((subject, index) => (
              <option key={index} value={subject}>
                {subject}
              </option>
            ))}
          </select>

          {/* Name filter */}
          <input
            type="text"
            placeholder="Ad"
            value={name}
            onChange={(e) => setName(e.target.value)}
          />

          {/* Surname filter */}
          <input
            type="text"
            placeholder="Soyad"
            value={surname}
            onChange={(e) => setSurname(e.target.value)}
          />

          {/* Job status filter */}
          <input
            type="text"
            placeholder="İş statusu"
            value={jobStatus}
            onChange={(e) => setJobStatus(e.target.value)}
          />

          {/* City filter */}
          <input
            type="text"
            placeholder="Şəhər"
            value={city}
            onChange={(e) => setCity(e.target.value)}
          />
        </div>
        <div className={styles.container_buttons}>
          {/* Search button */}
          <button className={styles.button_top} onClick={handleSearch}>
            Axtar
          </button>
          {/* Button to navigate to Add Anket */}
          <button className={styles.button_bottom} onClick={() => navigate("/add")}>
            Anket əlavə et
          </button>
        </div>
      </div>

      <div className={styles.search_boxes}>
        {/* Display filtered profiles */}
        {handleSearch().map((profile, index) => (
          <div key={index} className={styles.search_box}>
            <div className={styles.box_top}>
              <div className={styles.box_top_left}>
                <img
                  src={profile.image ? URL.createObjectURL(profile.image) : man}
                  alt=""
                />
              </div>
              <div className={styles.box_top_right}>
                <h6>{profile.firstname} {profile.lastname}</h6>
                <p className={styles.speciality}>{profile.subject}</p>
                <p>{profile.company}</p>
                <p className={styles.position}>{profile.position}</p>
              </div>
            </div>
            <div className={styles.box_bottom}>
              <div>
                <div>Proqramlar: {profile.skills}</div>
                <div>Sertifikatlar: {profile.certificates}</div>
              </div>
              <div className={styles.status}>
                <span className={styles.ish}>{profile.jobStatus}</span>
                <span className={styles.full}>ətraflı</span>
              </div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Search;
