import React, { useState } from "react";
import styles from "./Anket.module.css";


const Anket = ({ onSubmit, inputRef, options, setOptions  }) => {
  const [newOption, setNewOption] = useState("");

  const addOption = () => {
    if (newOption.trim() && !options.includes(newOption)) {
      setOptions([...options, newOption]); 
      setNewOption(""); 
    }
  };
 

  const [values, setValues] = useState({
    firstname: "",
    lastname: "",
    email: "",
    mobile: "",
    subject: "",
    gender: "",
    company: "",
    position: "",
    resume: null,
    image: null,
    url: "",
    skills: "",
    certificates: "",
    about: "",
    jobStatus: "İş axtarır",
  });

  const [isSubmitted, setIsSubmitted] = useState(false);
  

  const handleChanges = (e) => {
    const { name, type, value, files } = e.target;
    setValues({ ...values, [name]: type === "file" ? files[0] : value });
  };


  const handleSubmit = (e) => {
    e.preventDefault();
    onSubmit(values);
    setIsSubmitted(true);
  };

  const ResetFun = () => {
    setValues({
      firstname: "",
      lastname: "",
      email: "",
      mobile: "",
      subject: "",
      gender: "",
      company: "",
      position: "",
      resume: null,
      image: null,
      url: "",
      skills: "",
      certificates: "",
      about: "",
      jobStatus: "İş axtarır",
    });
  };

  return (
    <div className={styles.container}>
      {isSubmitted ? (
        <h1> Məlumatlar göndərildi. </h1>
      ) : (
        <form onSubmit={handleSubmit}>
          <h1>Məlumatları daxil edin:</h1>

          <select>
        <option value="">Sahəni seç</option>
        {options.map((opt, index) => (
          <option key={index} value={opt}>
            {opt}
          </option>
        ))}
      </select>
      <input
        type="text"
        value={newOption}
        onChange={(e) => setNewOption(e.target.value)}
        placeholder="Yeni sahə əlavə et"
      />
      <button onClick={addOption} className={styles.formButton} >Sahə əlavə et</button>
          <label>Ad*</label>
          <input ref={inputRef} value={values.firstname} type="text" name="firstname" required onChange={handleChanges} />

          <label>Soyad*</label>
          <input type="text" value={values.lastname} name="lastname" onChange={handleChanges} />

          <label>Email*</label>
          <input type="email" value={values.email} name="email" onChange={handleChanges} />

          <label>Mobil nömrə</label>
          <input type="text" value={values.mobile} name="mobile" onChange={handleChanges} />

          <label>Cinsi</label>
          <input type="radio" name="gender" value="Kişi" checked={values.gender === "Kişi"} onChange={handleChanges} /> Kişi
          <input type="radio" name="gender" value="Qadın" checked={values.gender === "Qadın"} onChange={handleChanges} /> Qadın


          <label>İş statusu</label>
          <select name="jobStatus" value={values.jobStatus} onChange={handleChanges}>
            <option value="İş axtarır">İş axtarır</option>
            <option value="İşləyir">İşləyir</option>
            <option value="Freelancer">Freelancer</option>
          </select>

          <label>İşlədiyi yer</label>
          <input type="text" name="company" onChange={handleChanges} />

          <label>Vəzifəsi</label>
          <input type="text" name="position" onChange={handleChanges} />

          <label>CV</label>
          <input type="file" id="resume" name="resume" onChange={handleChanges} />

          <label>Şəkil</label>
          <input type="file" id="image" name="image" accept="image/*" onChange={handleChanges} />

          <label>Linkedin</label>
          <input type="text" name="url" onChange={handleChanges} />

          <label>Biliklər</label>
          <textarea name="skills" value={values.skills} onChange={handleChanges}></textarea>

          <label>Sertifikatlar</label>
          <textarea name="certificates" value={values.certificates} onChange={handleChanges}></textarea>

          <label>Haqqında</label>
          <textarea name="about" value={values.about} onChange={handleChanges}></textarea>

          <button type="button" className={styles.formButton} onClick={ResetFun}>
            Yenilə
          </button>
          <button type="submit" className={styles.formButton}>
            Təsdiqlə
          </button>
        </form>
      )}


    </div>
    
  );
};

export default Anket;
