import React, { useState } from 'react';
import { BrowserRouter, Routes, Route } from 'react-router-dom';
import Home from "./components/Home/Home";
import Nav from "./components/Nav/Nav";
import Search from './components/Search/Search';
import Add from "./components/Add/Add";
import Specialist from "./components/Specialist/Specialist";
import Footer from "./components/Footer/Footer";
import Blog from './components/Blog/Blog';
import Anket from './components/Anket/Anket';

function App() {
  const [profiles, setProfiles] = useState([]); // State to store profiles
  const [subjects, setSubjects] = useState(["Front-End", "Back-End", "Cybersecurity", "Devops"]); // State to store subjects

  // Function to handle adding new profiles
  const handleAddProfile = (newProfile) => {
    setProfiles((prevProfiles) => [newProfile, ...prevProfiles]);
  };

  return (
    <BrowserRouter>
      <Nav />
      <Routes>
        <Route path="/" element={<Home />} />
        <Route path="/search" element={<Search profiles={profiles} subjects={subjects} />} /> {/* Pass subjects to Search */}
        <Route path="/add" element={<Add onAddProfile={handleAddProfile} />} />
        <Route path="/specialist" element={<Specialist />} />
        <Route path="/blog" element={<Blog />} />
        <Route path="/footer" element={<Footer />} />
        <Route path="/anket" element={<Anket setSubjects={setSubjects} />} /> {/* Pass setSubjects to Anket */}

      </Routes>
      <Footer />
    </BrowserRouter>
  );
}

export default App;
