import React, { useState } from "react";
import { BrowserRouter, Routes, Route } from "react-router-dom";
import Search from "./components/Search/Search";
import Add from "./components/Add/Add";

function App() {
  // Store subjects in App.js so both Anket and Search use the same list
  const [subjects, setSubjects] = useState([
    "Front-End",
    "Back-End",
    "Cybersecurity",
    "Pentester",
    "İnformasiya təhlükəsizliyi",
    "Java developer",
    "React developer",
  ]);

  // Function to add new subjects
  const handleAddSubject = (newSubject) => {
    if (newSubject.trim() && !subjects.includes(newSubject)) {
      setSubjects([...subjects, newSubject]);
    }
  };

  return (
    <BrowserRouter>
      <Routes>
        <Route path="/search" element={<Search subjects={subjects} />} />
        <Route path="/add" element={<Add onAddSubject={handleAddSubject} subjects={subjects} />} />
      </Routes>
    </BrowserRouter>
  );
}

export default App;
