import React, { useState } from "react";
import Search from "./Search"; // Import Search component
import styles from "./Anket.module.css";

const Anket = ({ onSubmit, inputRef }) => {
  const [values, setValues] = useState({
    firstname: "",
    lastname: "",
    email: "",
    mobile: "",
    subject: "", // Subject is stored here
    gender: "",
    company: "",
    position: "",
    resume: null,
    image: null,
    url: "",
    skills: "",
    certificates: "",
    about: "",
    jobStatus: "İş axtarır", 
  });

  const [isSubmitted, setIsSubmitted] = useState(false);
  const [subjects, setSubjects] = useState([
    "Front-End",
    "Back-End",
    "Cybersecurity",
    "Pentester",
    "İnformasiya təhlükəsizliyi",
    "Java developer",
    "React developer",
  ]);
  const [customSubject, setCustomSubject] = useState("");

  const handleChanges = (e) => {
    const { name, type, value, files } = e.target;
    setValues({ ...values, [name]: type === "file" ? files[0] : value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    onSubmit(values); // Call onSubmit with the form values
    setIsSubmitted(true);
  };

  const ResetFun = () => {
    setValues({
      firstname: "",
      lastname: "",
      email: "",
      mobile: "",
      subject: "Front-End",
      city: "",
      gender: "",
      company: "",
      position: "",
      resume: null,
      image: null,
      url: "",
      skills: "",
      certificates: "",
      about: "",
      jobStatus: "İş axtarır", 
    });
    setCustomSubject(""); 
    document.getElementById("resume").value = "";
    document.getElementById("image").value = "";
  };

  const handleAddSubject = () => {
    if (customSubject.trim() && !subjects.includes(customSubject)) {
      setSubjects([...subjects, customSubject]);
      setValues({ ...values, subject: customSubject }); 
      setCustomSubject(""); 
    }
  };

  return (
    <div className={styles.container}>
      {isSubmitted ? (
        <h1> Məlumatlar göndərildi. </h1>
      ) : (
        <form onSubmit={handleSubmit}>
          {/* Your form fields here... */}
          <label htmlFor="subject">Sahə</label>
          <select
            name="subject"
            value={values.subject}
            required
            onChange={handleChanges}
          >
            {subjects.map((sub, index) => (
              <option key={index} value={sub}>
                {sub}
              </option>
            ))}
          </select>

          <input
            type="text"
            value={customSubject}
            onChange={(e) => setCustomSubject(e.target.value)}
            placeholder="Yeni sahə əlavə et"
          />
          <button type="button" onClick={handleAddSubject}>Sahə əlavə et</button>

          {/* Submit button */}
          <button type="submit">Göndər</button>
        </form>
      )}

      {/* Pass the selected subject to the Search component */}
      <Search selectedSubject={values.subject} />
    </div>
  );
};

export default Anket;
