import React, { useState } from "react";
import styles from "./Anket.module.css";

const Anket = ({ onSubmit, inputRef }) => {
  const [values, setValues] = useState({
    firstname: "",
    lastname: "",
    email: "",
    mobile: "",
    subject: "",
    gender: "",
    company: "",
    position: "",
    resume: null,
    image: null,
    url: "",
    skills: "",
    certificates: "",
    about: "",
    jobStatus: "İş axtarır", 
  });

  const [isSubmitted, setIsSubmitted] = useState(false);
  const [subjects, setSubjects] = useState([
    "Front-End",
    "Back-End",
    "Cybersecurity",
    "Pentester",
    "İnformasiya təhlükəsizliyi",
    "Java developer",
    "React developer",
  ]);
  const [customSubject, setCustomSubject] = useState("");

  const handleChanges = (e) => {
    const { name, type, value, files } = e.target;
    setValues({ ...values, [name]: type === "file" ? files[0] : value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    onSubmit(values);
    setIsSubmitted(true);
  };

  const ResetFun = () => {
    setValues({
      firstname: "",
      lastname: "",
      email: "",
      mobile: "",
      subject: "Front-End",
      city: "",
      gender: "",
      company: "",
      position: "",
      resume: null,
      image: null,
      url: "",
      skills: "",
      certificates: "",
      about: "",
      jobStatus: "İş axtarır", 
    });
    setCustomSubject(""); 
    document.getElementById("resume").value = "";
    document.getElementById("image").value = "";
  };

  const handleAddSubject = () => {
    if (customSubject.trim() && !subjects.includes(customSubject)) {
      setSubjects([...subjects, customSubject]);
      setValues({ ...values, subject: customSubject }); 
      setCustomSubject(""); 
    }
  };

  return (
    <div className={styles.container}>
      {isSubmitted ? (
        <h1> Məlumatlar göndərildi. </h1>
      ) : (
        <form onSubmit={handleSubmit}>
          <h1>Məlumatları daxil edin:</h1>

          <label htmlFor="firstname">Ad*</label>
          <input
            ref={inputRef}
            value={values.firstname}
            type="text"
            name="firstname"
            placeholder="Adı daxil edin"
            required
            onChange={handleChanges}
          />

          <label htmlFor="lastname">Soyad*</label>
          <input
            type="text"
            value={values.lastname}
            name="lastname"
            placeholder="Soyadı daxil edin"
            required
            onChange={handleChanges}
          />

          <label htmlFor="email">Email*</label>
          <input
            type="email"
            value={values.email}
            name="email"
            placeholder="Email daxil edin"
            required
            onChange={handleChanges}
          />

          <label htmlFor="mobile">Mobil nömrə</label>
          <input
            type="text"
            value={values.mobile}
            name="mobile"
            placeholder="Mobil nömrə daxil edin"
            onChange={handleChanges}
          />

          <label htmlFor="gender">Cinsi</label>
          <input
            type="radio"
            name="gender"
            value="Kişi"
            checked={values.gender === "Kişi"}
            onChange={handleChanges}
          />{" "}
          Kişi
          <input
            type="radio"
            name="gender"
            value="Qadın"
            checked={values.gender === "Qadın"}
            onChange={handleChanges}
          />{" "}
          Qadın

          <input
            type="text"
            value={values.city}
            name="city"
            placeholder="Yaşadığı yeri daxil edin"
            required
            onChange={handleChanges}
          />

          <label htmlFor="subject">Sahə</label>
          <select
            name="subject"
            value={values.subject}
            required
            onChange={handleChanges}
          >
            {subjects.map((sub, index) => (
              <option key={index} value={sub}>
                {sub}
              </option>
            ))}
          </select>

          <input
            type="text"
            value={customSubject}
            onChange={(e) => setCustomSubject(e.target.value)}
            placeholder="Yeni sahə əlavə et"
          />
          <button type="button" onClick={handleAddSubject}>Sahə əlavə et</button>

          <label htmlFor="jobStatus">İş Statusu</label>
          <select
            value={values.jobStatus}
            onChange={handleChanges}
            name="jobStatus"
          >
            <option value="İş axtarır">İş axtarır</option>
            <option value="İşdədir">İşdədir</option>
          </select>

          <label htmlFor="image">Profil şəkli</label>
          <input
            type="file"
            name="image"
            id="image"
            onChange={handleChanges}
          />

          <button type="submit">Göndər</button>
        </form>
      )}
    </div>
  );
};

export default Anket;
